import React from 'react';

const ReportOverview = ({ selected, timeFilters, value, amounts }) => {

  const income = amounts
  .filter(item => item > 0)
  .reduce((acc, item) => (acc += item), 0)
  .toFixed(2);

  const expense = (
    amounts.filter(item => item < 0).reduce((acc, item) => (acc += item), 0) *
    -1
  ).toFixed(2);

  const balance = { 
    position: 'absolute', 
    top: '50%', left: '50%', 
    transform: 'translate(-50%, -50%)', 
    textAlign: 'center',
    width: '100%',
  }

  const incomeExpense = { 
    display: 'flex', 
    justifyContent: 'space-around',
    height: '100%', 
    width: '320px',
    margin: 'auto',
  }

  const textBlock = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-around',
    height: '100%',
    textAlign: 'right'
  }

  const chartBlock = {
    width: '60%',
    margin: 'auto',
    textAlign: 'center',
  }

  return (
    <div className='plus-bg' style={{ height: '30vh', position: 'relative' }}>
      {selected === 'all' ? (
        <div style={balance}>
          <Total text={`${timeFilters[value]}ly balance`} amounts={amounts} />
        </div>
      ) : (
        <div style={incomeExpense}>
          <div style={textBlock}>
            <IncomeExpenses amounts={amounts} />
          </div>
          <div style={chartBlock}>test</div>
        </div>
      )}
    </div>
  );
};

export default ReportOverview;
