import React, { Fragment, useState, useEffect, useContext } from 'react';
import moment from 'moment';
import { GlobalContext } from '../context/GlobalState';
import Transaction from './Transaction';
import BarChart from '../components/common/BarChart';
import { Tabs, Tab, Button, ButtonGroup, CircularProgress } from '@material-ui/core';

const Statistics = () => {
  const { loading, transactions, getTransactions } = useContext(GlobalContext);
  const [value, setValue] = useState(0);
  // const [selected, setSelected] = useState('income');
  const today = moment();
  const timeFilters = ['week', 'month', 'year'];
  // const transFilters = ['income', 'expense'];
  const incomeLists = [],
        expenseLists = [],
        combinedLists = [];
  const lists = [
    { type: 'income', container: incomeLists },
    { type: 'expense', container: expenseLists },
  ];

  // const filterAmount = type => {
  //   return transactions
  //     .filter(transaction => {
  //       if (type === 'expense') return transaction.amount < 0;
  //       if (type === 'income') return transaction.amount > 0;
  //       return transaction.amount;
  //     })
  // }

  const filterDate = time => {
    return transactions
      .filter(transaction => moment(transaction.date).isSame(today, time));
      // .filter(transaction => {
      //   if (type === 'expense') return transaction.amount < 0;
      //   if (type === 'income') return transaction.amount > 0;
      //   return transaction.amount;
      // });
  };

  const sumAmount = (time, filter, order) => {
    return Object.values(
      filterDate(time).reduce((result, { date, amount }) => {
        const index = (date) => moment(date).format(order);
        const format = (date) => moment(date).format(filter);

        !result[format(date)]
          ? (result[format(date)] = { 
              index: +index(date), 
              text: format(date), 
              income: amount > 0 ? amount : 0,
              expense: amount < 0 ? amount : 0,
            })
          : (amount > 0 
              ? result[format(date)].income += amount 
              : result[format(date)].expense += amount
            );
        console.log(result)
        return result;
      }, {})
    );
  };

  if (value === 0) {
    lists.map((list) =>
      list['container'].push(...sumAmount('week', 'dddd', 'e'))
    );
    combinedLists.push(...incomeLists, ...expenseLists);

  } else if (value === 1) {
    const filter = 'D MMM';
    const sunday = weekNum => moment().day(0).week(weekNum).format(filter);
    const saturday = weekNum => moment().day(6).week(weekNum).format(filter);
    const formatWeek = weekNum => `${sunday(weekNum)} - ${saturday(weekNum)}`;

    lists.map((list) =>
      list['container'].push(...sumAmount('month', 'w', 'w'))
    );
    combinedLists.push(...incomeLists, ...expenseLists);
    combinedLists.forEach(list => list['text'] = formatWeek(list.index));

  } else if (value === 2) {
    lists.map((list) =>
      list['container'].push(...sumAmount('year', 'MMMM', 'MM'))
    );
    combinedLists.push(...incomeLists, ...expenseLists);
  }

  useEffect(() => {
    getTransactions();
    // eslint-disable-next-line
  }, []);

  return (
    <Fragment>
      <div className='plus-bg time-bar'>
        <Tabs value={value} variant='fullWidth' aria-label='time selectors'>
          {timeFilters.map((timeFilter, index) => (
            <Tab
              key={timeFilter}
              label={timeFilter}
              onClick={() => setValue(index)}
              disableFocusRipple
              disableRipple
            />
          ))}
        </Tabs>
      </div>

      <div className='plus-bg box'>
        <div className='box-incomeExpense'>
          <BarChart data={combinedLists} select={value} width='350' height='145' />
        </div>
      </div>


      <div className='container'>

        {/* <ButtonGroup
          fullWidth disableRipple
          color='primary'
          style={{ borderRadius: 0 }}
          aria-label='transaction amount filters'
        >
          {transFilters.map(transFilter => (
            <Button
              key={transFilter}
              variant='contained'
              color={selected === transFilter ? 'primary' : 'secondary'}
              onClick={() => setSelected(transFilter)}
              style={{ 
                borderRadius: 0,
                color: selected === transFilter ? 'white' : '#65BCBF'
                // border: '1px solid rgba(255, 255, 255, 0.3)'
              }}
              disableElevation disableFocusRipple disableRipple
            >
              {transFilter}
            </Button>
          ))}
        </ButtonGroup> */}


        {transactions.length > 0 ? (
          <ul className='list'>
            {combinedLists
              .sort((a, b) => (a.index < b.index ? -1 : 1))
              .map((list, index) => (
                <Transaction key={index} transaction={list} />
              ))}
            {combinedLists.length === 0 && (
              <div className='list-status'>
                No expense transaction<br />of the {timeFilters[value]}
              </div>
            )}
          </ul>
        ) : (
          <div className='list-status'>
            {loading ? <CircularProgress color='primary' /> : 'No transaction'}
          </div>
        )}
      </div>
    </Fragment>
  );
};

export default Statistics;
