import React from 'react';
import * as d3 from 'd3';
import Total from './common/Total';
import { numberEuro } from '../utils/format';
// import DataUsageSharpIcon from '@material-ui/icons/DataUsageSharp';

const ReportOverview = ({ selected, timeFilters, value, amounts }) => {

  const income = amounts
  .filter(item => item > 0)
  .reduce((acc, item) => (acc += item), 0)
  .toFixed(2);

  const expense = (
    amounts.filter(item => item < 0).reduce((acc, item) => (acc += item), 0) *
    -1
  ).toFixed(2);

  const data = [income, expense];

  const width = 120;
  const height = 120;
  const radius = width / 2;

  const svg = d3.select('#chart')
    .append('svg')
    .attr('width', width)
    .attr('height', height)

  const pie = d3.pie().value(d => d);
  const arc = d3.arc().outerRadius(radius).innerRadius(radius - 5);

  const arcs = svg.selectAll('.arc')
    .data(pie(data))
    .enter()
    .append('g')
    .attr('class', 'arc')

  const balance = { 
    position: 'absolute', 
    top: '50%', left: '50%', 
    transform: 'translate(-50%, -50%)', 
    textAlign: 'center',
    width: '100%',
  }

  const incomeExpense = { 
    display: 'flex', 
    justifyContent: 'space-around',
    height: '100%', 
    width: '320px',
    margin: 'auto',
  }

  const textBlock = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-around',
    height: '100%',
    textAlign: 'right'
  }

  const chartBlock = {
    // width: '50%',
    margin: 'auto 0',
    textAlign: 'center',
  }

  return (
    <div className='plus-bg' style={{ height: '25vh', position: 'relative' }}>
      {selected === 'all' ? (
        <div style={balance}>
          <Total text={`${timeFilters[value]}ly balance`} amounts={amounts} />
        </div>
      ) : (
        <div style={incomeExpense}>
          <div style={textBlock}>
            <div>
              <p className='sub-title'>Income</p>
              <p className='sub-amount'>+{numberEuro(income)}</p>
            </div>
            <div>
              <p className='sub-title'>Expense</p>
              <p className='sub-amount'>-{numberEuro(expense)}</p>
            </div>
          </div>

          <div id='chart' style={chartBlock}>

          </div>
        </div>
      )}
    </div>
  );
};

export default ReportOverview;
