import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const BarChart = ({ data : originalData, width, height }) => {
  const ref = useRef(null);
  // const cache = useRef(originalData);

  const colors = [
    'rgba(255, 255, 255, 1)',
    'rgba(255, 255, 255, 0.3)'
  ];

  useEffect(() => {
    const data = originalData;
    const labels = originalData.map(d => d.text);
    // const prevData = createPie(cache.current);
    const group = d3.select(ref.current);

    const scaleY = d3
      .scaleLinear()
      .domain([0, Math.max(...data)])
      .range([0, height])

    const scaleX = d3
      .scaleBand()
      .domain(labels)
      .range([0, width])

    const groupWithData = group
      .selectAll('g.rect')
      .data(data);

    groupWithData.exit().remove();

    const groupWithUpdate = groupWithData
      .enter()
      .append('g')
      .attr('class', 'rect')
      .merge(groupWithData.select('rect'));

    groupWithUpdate
      .append('rect')
      .attr('width', 6)
      .attr('height', d => scaleY(d))
      .attr('fill', d => colors[0])
      .attr('x', (d, i) => scaleX(d.text))
      .attr('y', d => height - scaleY(d));

    // const path = groupWithUpdate
    //   .append('path')
    //   .merge(groupWithData.select('path.arc'));

    // path
    //   .attr('class', 'arc')
    //   .attr('d', createArc)
    //   .attr('fill', (d, i) => colors[i])
    //   .transition()
    //   .duration(750)
    //   .attrTween('d', arcTween);

  // eslint-disable-next-line
  }, [originalData])

  return ( 
    <svg width={width} height={height}>
      <g 
        ref={ref} 
        // transform={`translate(${width / 2} ${height / 2})`} 
      />
    </svg>
  );
}

export default BarChart;
