import React, { useState, useContext } from 'react';
import { GlobalContext } from '../context/GlobalState';
import TextField from '@material-ui/core/TextField';

const Login = () => {
  const { addUser, getUser, authUser } = useContext(GlobalContext);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = e => {
    e.preventDefault();

    const newUser = {
      id: Math.floor(Math.random() * 100000000),
      name,
      email,
      password,
    };
    addUser(newUser);
    // getUser(newUser);
    // console.log(newUser);

    setName('');
    setEmail('');
    setPassword('');

    // redirect to dashboard
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    authUser({ email, password });
    await getUser({ email, password });

    setEmail('');
    setPassword('');
  }

  return ( 
    <form 
      // onSubmit={e => handleSubmit(e)}
      className="new-form" 
      style={{ top: '100px auto' }}
      noValidate autoComplete="off"
    >

      <TextField
        id="standard-full-width"
        label="Acount" fullWidth required autoFocus
        value={name}
        onChange={e => {setName(e.target.value)}}
      />

      <TextField
        id="standard-full-width"
        label="email" fullWidth required
        value={email}
        onChange={e => setEmail(e.target.value)}
      />

      <TextField
        id="standard-full-width"
        label="password" fullWidth required
        value={password}
        onChange={e => setPassword(e.target.value)}
      />

      <button 
        onClick={e => handleSubmit(e)} 
        className='btn plus-bg'
        style={{ marginTop: '50px' }}
      >
        Sign up
      </button>

      <button 
        onClick={e => handleLogin(e)} 
        className='btn plus-bg'
      >
        Log in
      </button>

    </form>
  );
}

export default Login;
