import React, { useState, useContext } from 'react';
import { GlobalContext } from '../context/GlobalState';
import { FormControl, InputLabel, InputAdornment, Input, TextField, IconButton } from '@material-ui/core';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';

const Login = () => {
  const { registerUser, loginUser, error } = useContext(GlobalContext);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowpassword] = useState(false);

  const [errorName, setErrorName] = useState(false);
  const [errorEmail, setErrorEmail] = useState(false);
  const [errorPassword, setErrorPassword] = useState(false);

  const handleRegister = async (e) => {
    e.preventDefault();

    const newUser = {
      name,
      email,
      password,
    };
    await registerUser(newUser);
    if (error) {
      setName('');
      setEmail('');
      setPassword('');
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    await loginUser({ email, password });
    if (error) {
      setEmail('');
      setPassword('');
    }
  }

  return ( 
    <form
      className="new-form" 
      style={{ margin: '100px auto' }}
      noValidate autoComplete="off"
    >
      Signup to have your personal expense tracker now
      {error && <p>{error}</p>}
      <TextField
        id="standard-full-width"
        label="Acount" fullWidth required autoFocus
        value={name} error={errorName}
        onChange={e => {setName(e.target.value)}}
        helperText={errorName && "Please describe the transaction"}
      />

      <TextField
        id="standard-full-width"
        label="email" fullWidth required
        value={email} error={errorEmail}
        onChange={e => setEmail(e.target.value)}
        helperText={errorEmail && "Please describe the transaction"}
      />

      <Input
        id="standard-full-width"
        label="password" type="password" fullWidth required
        value={password} error={errorPassword}
        onChange={e => setPassword(e.target.value)}
        helperText={errorPassword && "Please describe the transaction"}
      />

      <FormControl>
        <InputLabel htmlFor="standard-adornment-password">Password</InputLabel>
        <Input
          id="standard-adornment-password"
          type={showPassword ? 'text' : 'password'}
          value={password}
          onChange={e => setPassword(e.target.value)}
          endAdornment={
            <InputAdornment position="end">
              <IconButton
                aria-label="toggle password visibility"
                onClick={() => setShowpassword(!showPassword)}
                onMouseDown={() => setShowpassword(!showPassword)}
              >
                {showPassword ? <Visibility /> : <VisibilityOff />}
              </IconButton>
            </InputAdornment>
          }
        />
      </FormControl>

      <button 
        onClick={e => handleRegister(e)} 
        className='btn plus-bg'
        style={{ marginTop: '50px' }}
      >
        Sign up
      </button>

      <button 
        onClick={e => handleLogin(e)} 
        className='btn plus-bg'
      >
        Log in
      </button>

    </form>
  );
}

export default Login;
