import React, { useState, useContext, useEffect } from 'react';
import { GlobalContext } from '../context/GlobalState';
import Transaction from './Transaction';
import Button from '@material-ui/core/Button';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import transitions from '@material-ui/core/styles/transitions';

const TransactionList = () => {
  const { transactions, getTransactions } = useContext(GlobalContext);
  const [selected, setSelected] = useState('all');

  const transFilters = ['all', 'incomce', 'expense'];

  useEffect(() => {
    getTransactions();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div>
      <ButtonGroup 
        fullWidth color="primary" 
        aria-label="outlined primary button group"
        style={{ marginBottom: '10px' }}
      >
        {transFilters.map(transFilter =>
          <Button 
            disableElevation
            variant={selected === transFilter ? 'contained' : null} 
            color={selected === transFilter ? 'primary' : null}  
            onClick={() => setSelected(transFilter)}
          >
            {transFilter}
          </Button>
        )}
        {/* <Button
          disableElevation
          variant={selected === 'income' ? 'contained' : null} 
          color={selected === 'income' ? 'primary' : null}  
          onClick={() => setSelected('income')}
        >
          income
        </Button>
        <Button
          disableElevation
          variant={selected === 'expense' ? 'contained' : null} 
          color={selected === 'expense' ? 'primary' : null}  
          onClick={() => setSelected('expense')}
        >
          expense
        </Button> */}
      </ButtonGroup>

      {transactions.length > 0 
        ? <ul className='list'>
            {selected === 'all' && transactions
              .map(transaction => (
                <Transaction key={transaction._id} transaction={transaction} />
              ))
            }
            {selected === 'income' && transactions
              .filter(transaction => transaction.amount > 0)
              .map(transaction => (
                <Transaction key={transaction._id} transaction={transaction} />
              ))
            }
            {selected === 'expense' && transactions
              .filter(transaction => transaction.amount < 0)
              .map(transaction => (
                <Transaction key={transaction._id} transaction={transaction} />
              ))
            }
            <div style={{ height: '56px' }} /> 
          </ul>
        : <div>No transaction</div>
      }

    </div>
  );
};

export default TransactionList;
