import React, { useState, useContext, useCallback, useRef } from 'react';
import { Transition, useSpring, useChain, config, animated } from 'react-spring';
import { GlobalContext } from '../../context/GlobalState';
import { numberEuro, formatAmount } from '../../utils/format';
import { getWeekDate } from '../../utils/calculation';
import moment from 'moment';
import DeleteSharpIcon from '@material-ui/icons/DeleteSharp';

const Transaction = ({ transaction, date, menu }) => {
  const { deleteTransaction } = useContext(GlobalContext);
  const [showMenu, setshowMenu] = useState(false);
  const [deleted, setDeleted] = useState(false);

  const sign = transaction.amount === 0 
    ? null : transaction.amount < 0 ? '-' : '+';

  const income = formatAmount(transaction.income),
        expense = formatAmount(transaction.expense);

  const buttonWidth = 70, 
        paddingLeft = 15;

  const listBlock = {
    cursor: menu && 'pointer',
    transform: menu && showMenu 
      ? `translateX(${paddingLeft + buttonWidth}px)` 
      : 'translateX(0)',
    transition: 'transform .3s ease',
  };

  const menuBtn = {
    position: 'absolute',
    width: buttonWidth,
    height: '100%',
    cursor: 'pointer',
  }

  const deleteBtn = {
    background: '#f8777d',
    left: `-${paddingLeft + buttonWidth}px`,
    ...menuBtn,
  }

  // const editBtn = {
  //   background: '#232c2d9f',
  //   left: `-${paddingLeft + buttonWidth}px`,
  //   ...menuBtn,
  // }

  const listText = { minWidth: '80px' };
  const listSubText = { fontSize: '12px' };

  const handleShowMenu = useCallback(() => {
    setshowMenu(!showMenu);
  }, [showMenu]);

  const prop = useSpring({
    transform: showMenu 
      ? `translate3d(${paddingLeft + buttonWidth}px, 0, 0)`
      : 'translate3d(0px,0,0)',
    cursor: 'pointer',
    config: config.stiff,
  });

  // const transRef = useRef();
  // const transition = useTransition(deleted && transaction, item => item._id, {
  //   ref: transRef,
  //   from: { height: 86, transform: 'translate3d(-5%,0,0)', opacity: 0 },
  //   enter: { height: 86, transform: 'translate3d(0%,0,0)', opacity: 1 },
  //   leave: { height: 0, transform: 'translate3d(-50%,0,0)', opacity: 0 },
  //   trail: 200,
  // });

  // useChain(showMenu ? [])

  return (
    <Transition
      items={deleted}
      from={{ height: 86, transform: 'translate3d(-5%,0,0)', opacity: 0 }}
      enter={{ height: 86, transform: 'translate3d(0%,0,0)', opacity: 1 }}
      leave={{ height: 0, transform: 'translate3d(-200%,0,0)', opacity: 0 }}
    >
      {deleted => 
        deleted && (props => <animated.li style={{ ...props, ...prop }} onClick={handleShowMenu}>
        {menu && (
          <>
            <div 
              style={deleteBtn} 
              onClick={() => {
                deleteTransaction(transaction._id); 
                setDeleted(true);
              }}
            >
              <DeleteSharpIcon className='menu-icon' />
            </div>
            {/* <div style={editBtn}>
              <EditSharpIcon className='menu-icon' />
            </div> */}
          </>
        )}

        <div style={listText}>
          <p>{transaction.text}</p>
          {transaction.text.startsWith('Week') && (
            <p className='list-date' style={listSubText}>
              {getWeekDate(transaction.index)}
            </p>
          )}
          {date && (
            <p className='list-date'>
              {moment(transaction.date).format('D MMM, YYYY')}
            </p>
          )}
        </div>

        {transaction.amount ? (
          <span
            className={`list-amount ${
              transaction.amount === 0 ? null
                : transaction.amount > 0 ? 'plus' : 'minus'
            }`}
          >
            {sign}{numberEuro(Math.abs(transaction.amount))}
          </span>
        ) : (
          <div className='list-amount block-amount'>
            <span className='block plus'>
              {transaction.income === 0 ? '-' : '+' + numberEuro(income)}
            </span>
            <span className='block minus expense-amount'>
              {transaction.expense === 0 ? '-' : '-' + numberEuro(expense)}
            </span>
          </div>
        )}
      </animated.li>)}
    </Transition>
  );
};

export default Transaction;
