import React, { Fragment, useState, useEffect, useContext } from 'react';
import { GlobalContext } from '../context/GlobalState';
import { checkWeek, checkDay, checkMonth, checkYear, sortDateDsc, sortDateAsc, sortAmountDsc, sortAmountAsc } from '../utils/calculation';
import Transaction from './Transaction';
import { Tabs, Tab, CircularProgress } from '@material-ui/core';

const Statistics = () => {
  const { loading, transactions, getTransactions } = useContext(GlobalContext);
  const [value, setValue] = useState(0);
  const [selected, setSelected] = useState('all');
  const [sortColumn, setSortColum] = useState('date');
  const [sortLatest, setSortLatest] = useState(true);
  const [sortDsc, setSortDsc] = useState(true);

  const timeFilters = ['week', 'month', 'year'];
  const transFilters = ['all', 'income', 'expense'];
  const incomeLists = [];
  const expenseLists = [];
  let counter = 0;

  console.log(expenseLists)
  useEffect(() => {
    getTransactions();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return ( 
    <Fragment>
      <div className='plus-bg time-bar'>
        <Tabs value={value} variant="fullWidth" aria-label="time selectors">
          {timeFilters.map((timeFilter, index) => 
            <Tab 
              key={timeFilter} label={timeFilter} 
              onClick={() => setValue(index)} 
              disableFocusRipple disableRipple
            />
          )}
        </Tabs>
      </div>

      <div className='plus-bg box'>
        <div className='container box-incomeExpense'>

        </div>
      </div>

      <div className="container">
        {transactions.length > 0 ? (
          <ul className='list'>
            {transactions
              .filter(transaction => {
                const date = transaction.date;
                if (value === 0) return checkDay(date);
                if (value === 1) return checkWeek(date);
                if (value === 2) return checkMonth(date);
                if (value === 3) return checkYear(date);
                return transaction;
              })
              .filter(transaction => {
                switch (selected) {
                  case 'income':
                    return transaction.amount > 0;
                  case 'expense':
                    return transaction.amount < 0;
                  default:
                    return transaction;
                }
              })
              .sort((a, b) => {
                if (sortColumn === 'date') {
                  return sortLatest ? sortDateDsc(a, b) : sortDateAsc(a, b);
                }
                return sortDsc ? sortAmountDsc(a, b) : sortAmountAsc(a, b);
              })
              .map(transaction => {
                transaction.amount > 0 ? incomeLists.push(transaction.amount) : expenseLists.push(transaction.amount);
                counter++;
                return (
                  <Transaction key={transaction._id} transaction={transaction} date />
                );
              })}
            {counter === 0 && (
              <div className='list-status'>
                No {selected !== 'all' && selected} transaction<br/>
                of this {timeFilters[value]}
              </div>
            )}
          </ul>
        ) : (
          <div className='list-status'>
            {loading 
              ? <CircularProgress color='primary'/>
              : 'No transaction'}
          </div>
        )}
      </div>
    </Fragment>
  );
}

export default Statistics;
