import React from 'react';
import Total from './common/Total';
import PieChart from './common/PieChart';
import { numberEuro } from '../utils/format';
import { Typography } from '@material-ui/core';

const ReportOverview = ({ selected, timeFilters, value, amounts }) => {

  const income = amounts
    .filter(item => item > 0)
    .reduce((acc, item) => (acc += item), 0)
    .toFixed(2);

  const expense = (amounts
    .filter(item => item < 0)
    .reduce((acc, item) => (acc += item), 0) * -1)
    .toFixed(2);

  const ratio = income === 0 ? '100' : (expense / income * 100).toFixed(0);

  const data = [ratio, 100 - ratio];
  const width = 120, height = 120;
  const outerRadius = width / 2;
  const innerRadius = outerRadius - 6;

  const balance = { 
    position: 'absolute', 
    top: '50%', left: '50%', 
    transform: 'translate(-50%, -50%)', 
    textAlign: 'center',
    width: '100%',
  }

  const incomeExpense = { 
    display: 'flex', 
    // justifyContent: 'space-around',
    height: '100%', 
    // width: '250px',
    margin: 'auto',
  }

  const textBlock = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-around',
    height: '100%',
    textAlign: 'right',
    minWidth: '150px',
  }

  const chartBlock = {
    // width: '50%',
    margin: 'auto',
  }

  return (
    <div className='plus-bg' style={{ height: '28vh', position: 'relative', padding: '20px 0' }}>
      {selected === 'all' ? (
        <div style={balance}>
          <Total text={`${timeFilters[value]}ly balance`} amounts={amounts} />
        </div>
      ) : (
        <div className='container' style={incomeExpense}>
          <div style={chartBlock}>
            <Typography>{ratio}%</Typography>
            <PieChart
              data={data}
              width={width}
              height={height}
              outerRadius={outerRadius}
              innerRadius={innerRadius}
            />
          </div>

          <div style={textBlock}>
            <div>
              <p className='sub-title'>Income</p>
              <p className='sub-amount'>+{numberEuro(income)}</p>
            </div>
            <div>
              <p className='sub-title'>Expense</p>
              <p className='sub-amount'>-{numberEuro(expense)}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ReportOverview;
