import React, { useState, useContext } from 'react';
import { GlobalContext } from '../context/GlobalState';
import { InputLabel, InputAdornment, Input, TextField, IconButton } from '@material-ui/core';
import Alert from '@material-ui/lab/Alert';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
  },
}));

const Login = () => {
  const { registerUser, loginUser, error } = useContext(GlobalContext);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowpassword] = useState(false);
  const [disableBtn, setDisableBtn] = useState(true);
  const [showSignup, setShowSignup] = useState(false);

  const classes = useStyles();

  const handleRegister = async (e) => {
    e.preventDefault();

    const newUser = {
      name,
      email,
      password,
    };
    await registerUser(newUser);
    if (error) {
      setName('');
      setEmail('');
      setPassword('');
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    await loginUser({ email, password });
    if (error) {
      setEmail('');
      setPassword('');
    }
  }

  return ( 
    <form
      className="new-form" 
      style={{ margin: '100px auto' }}
      noValidate autoComplete="off"
    >
      {error && <Alert severity="error" className={classes.root}>{error}</Alert>}
      {showSignup && <TextField
        id="standard-full-width"
        label="Name" fullWidth required autoFocus
        value={name}
        onChange={e => setName(e.target.value)}
      />}

      <TextField
        id="standard-full-width"
        label="Email" type="email" fullWidth required
        value={email}
        onChange={e => setEmail(e.target.value)}
      />

      <TextField
        id="standard-full-width"
        label="Password" fullWidth required
        type={showPassword ? 'text' : 'password'}
        value={password}
        onChange={e => setPassword(e.target.value)}
        InputProps={{
          endAdornment:
            (<InputAdornment position="end">
              <IconButton
                aria-label="toggle password visibility"
                onClick={() => setShowpassword(!showPassword)}
                onMouseDown={() => setShowpassword(!showPassword)}
              >
                {showPassword ? <Visibility /> : <VisibilityOff />}
              </IconButton>
            </InputAdornment>)
        }}
      />

      {showSignup 
        ? <button 
            onClick={e => handleRegister(e)} 
            className='btn plus-bg'
            disabled={error ? true : false}
          >
            Sign up
          </button>
        : <button 
            onClick={e => handleLogin(e)} 
            className='btn plus-bg'
            disabled={error ? true : false}
          >
            Log in
          </button>
      }
      Do not have an account? 
      <b onClick={() => setShowSignup(true)} style={{ cursor: 'pointer' }}>Signup</b>

    </form>
  );
}

export default Login;
