import React, { useEffect, useRef } from 'react';
import { select, arc, pie } from 'd3';

const PieChart = ({ data, width, height, outerRadius, innerRadius }) => {
  const ref = useRef(null);
  const colorList = [
    'rgba(255, 255, 255, 1)', 
    'rgba(255, 255, 255, 0.3)'
  ];

  useEffect(() => {
    const svg = select(ref.current);

    const createArc = arc()
      .outerRadius(outerRadius)
      .innerRadius(innerRadius);

    const createPie = pie()
      .value((d) => d)
      .sort(null);

    const instructions = createPie(data);

    svg
      .selectAll('.slice')
      .data(instructions)
      .join('path')
      .attr('class', 'slice')
      .attr('fill', (d, i) => colorList[i])
      .transition()
      .attr('d', instruction => createArc(instruction));

    // eslint-disable-next-line
  }, [data]);

  return (
    <svg width={width} height={height}>
      <g ref={ref} transform={`translate(${outerRadius} ${outerRadius})`} />
    </svg>
  );
};

export default PieChart;
