import React, { useState, useContext, Fragment } from 'react';
import { GlobalContext } from '../context/GlobalState';
import PasswordIcon from '../components/common/PasswordIcon';
import { TextField, Typography } from '@material-ui/core';
import Alert from '@material-ui/lab/Alert';

import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  form: {
    margin: 'auto',
    width: '80%',
    marginTop: '30%',
    textAlign: 'center',
  },
  alert: {
    width: '100%',
    align: 'left',
    top: '56px',
  },
  input: {
    margin: '15px auto'
  },
  button: {
    marginTop: '30px'
  },
  link: {
    display: 'inline',
    cursor: 'pointer',
    marginLeft: '10px'
  }
}));

const Login = () => {
  const { registerUser, loginUser, error } = useContext(GlobalContext);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowpassword] = useState(false);
  const [disableBtn, setDisableBtn] = useState(true);
  const [showSignup, setShowSignup] = useState(false);

  const classes = useStyles();

  const handleRegister = async e => {
    e.preventDefault();

    const newUser = {
      name,
      email,
      password
    };
    await registerUser(newUser);
    if (error) {
      setName('');
      setEmail('');
      setPassword('');
    }
  };

  const handleLogin = async e => {
    e.preventDefault();

    await loginUser({ email, password });
    if (error) {
      setEmail('');
      setPassword('');
    }
  };

  return (
    <Fragment>
    {error && (
      <Alert severity='error' className={classes.alert}>
        {error}
      </Alert>
    )}
    <form
      className={classes.form}
      noValidate
      autoComplete='off'
    >

      <TextField
        label={showSignup ? 'Name' : ''}
        fullWidth
        required
        disabled={showSignup? false : true}
        value={name}
        onChange={e => setName(e.target.value)}
        className={classes.input}
      />

      <TextField
        label='Email'
        type='email'
        fullWidth
        required
        value={email}
        onChange={e => setEmail(e.target.value)}
        className={classes.input}
      />

      <TextField
        label='Password'
        fullWidth
        required
        type={showPassword ? 'text' : 'password'}
        value={password}
        onChange={e => setPassword(e.target.value)}
        className={classes.input}
        InputProps={{
          endAdornment: (
            <PasswordIcon
              showPassword={showPassword}
              setShowpassword={() => setShowpassword(!showPassword)}
            />
          )
        }}
      />

      {showSignup ? (
        <>
          <button
            onClick={e => handleRegister(e)}
            className={`btn plus-bg ${classes.button}`}
            // disabled={disableBtn ? true : false}
          >
            Sign up
          </button>
          <div className={classes.button}>
            Already have an account?
            <Typography
              color='primary'
              className={classes.link}
              onClick={() => setShowSignup(!showSignup)}
            >
              Login
            </Typography>
          </div>
        </>
      ) : (
        <>
          <button
            onClick={e => handleLogin(e)}
            className={`btn plus-bg ${classes.button}`}
            // disabled={disableBtn ? true : false}
          >
            Log in
          </button>
          <div className={classes.button}>
            Do not have an account?
            <Typography
              color='primary'
              className={classes.link}
              onClick={() => setShowSignup(!showSignup)}
            >
              Signup
            </Typography>
          </div>
        </>
      )}
    </form>
    </Fragment>
  );
};

export default Login;
