import React, { Fragment } from 'react';
import {useTransition, animated} from 'react-spring';
import { numberEuro } from '../../utils/format';

const Total = ({ amounts, text = 'total balance' }) => {
  const total = +amounts.reduce((acc, item) => (acc += item), 0).toFixed(2);
  const sign = total === 0 ? null : total < 0 ? '-' : '+';

  const transitions = useTransition(total, null, {
    from: { transform: 'translate3d(0,-40px,0)' },
    enter: { transform: 'translate3d(0,0px,0)' },
    leave: { transform: 'translate3d(0,-40px,0)' },
  });

  return (
    <Fragment>
      <p className='main-title'>{text}</p>
      {transitions.map(({ item, key, props }, index) => (
        <p className='main-amount' key={index} style={props}>
          {sign}â‚¬{numberEuro(Math.abs(item))}
        </p>
      ))}
    </Fragment>
  );
};

export default Total;
