import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Switch from '@material-ui/core/Switch';

const switchStyle = withStyles(theme => ({
  root: {
    marginRight: -12,
  },
  switchBase: {
    color: theme.palette.primary.main,
    '&$checked': {
      color: theme.palette.primary.main,
    },
    '&$checked + $track': {
      backgroundColor: theme.palette.primary.main,
    },
  },
  thumb: {
    boxShadow: 'none'
  },
  checked: {},
  track: {
    backgroundColor: theme.palette.primary.main,
  },
}))(Switch);

const TransactionSwitch = ({ minus, setMinus, onChange }) => {
  return ( 
    <div
      checked={minus}
      onChange={onChange}
      inputProps={{ 'aria-label': 'secondary checkbox', className: switchStyle }}
    />
  );
}

export default TransactionSwitch;
