import { useContext, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { GlobalContext } from '../../context/GlobalState';

const { getToken } = useContext(GlobalContext);
let token = null;

useEffect(() => {
  token = getToken();
}, [])

const drawerWidth = 240;

const currentHours = new Date().getHours();
const dayTime = !currentHours < 4 && currentHours < 18;

export const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
  },
  drawer: {
    [theme.breakpoints.up('sm')]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    boxShadow: 'none',
    [theme.breakpoints.up('sm')]: {
      width: token && `calc(100% - ${drawerWidth}px)`,
      marginLeft: token && drawerWidth,
      display: 'none',
    },
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up('sm')]: {
      display: 'none',
    },
    color: 'white',
  },
  drawerPaper: {
    width: drawerWidth,
    background: dayTime ? '#EAEBED' : '#232c2d',
    // background: '#F3F5F4',
  },
  content: {
    flexGrow: 1,
  },
  textColor: {
    opacity: 0.8,
    color: dayTime ? '#232c2d' : 'white',
    textDecoration: 'none',
  },
  menuIcon: {
    marginRight: '15px',
    opacity: 0.5,
  }
}));
