export function numberEuro(x) {
  return x.toString()
    .replace(/\./, ",")
    .replace(/\B(?=(\d{3})+(?!\d))/g, ".");
}

export function numberValid(x) {
  return /^(\d+|\d{1,3}(\.\d{3})*)(,\d{1,2})?$/.test(x);
}

export function numberCalc(x) {
  return Number(x.replace(/\./g, '').replace(/,/, '.'));
}

export function formatDate(date) {
  let d = new Date(date),
      month = '' + (d.getMonth() + 1),
      day = '' + d.getDate(),
      year = d.getFullYear();

  if (month.length < 2) 
      month = '0' + month;
  if (day.length < 2) 
      day = '0' + day;

  return [day, month, year].join('/');
}

export function checkDate(date) {
  let d = new Date(date),
      month = '' + (d.getMonth() + 1),
      day = '' + d.getDate(),
      year = d.getFullYear();

  if (month.length < 2) 
      month = '0' + month;
  if (day.length < 2) 
      day = '0' + day;

  return [String(year), month, day];
}

export function checkWeek(date) {
  let d = new Date();
  let first = d.getDate() - d.getDay();
  let last = first + 6;

  let firstday = new Date(d.setDate(first)).getTime();
  let lastday = new Date(d.setDate(last)).getTime();

  if (date >= firstday && date <= lastday) return true;
  return false;
}
