import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const BarChart = ({ data : originalData, width, height }) => {
  const ref = useRef(null);
  // const cache = useRef(originalData);

  const colors = [
    'rgba(255, 255, 255, 1)',
    'rgba(255, 255, 255, 0.3)'
  ];

  useEffect(() => {
    const data = originalData;
    // const prevData = createPie(cache.current);
    const group = d3.select(ref.current);
    const labels = data.map(d => d.text);
    const maxAmount = d3.max(data, d => Math.abs(d.amount));

    const yScale = d3
      .scaleLinear()
      .domain([0, maxAmount])
      .range([0, height]);

    const xScale = d3
      .scaleBand()
      .domain(labels)
      .range([0, width]);

    const xAxis = d3
      .axisBottom(xScale)
      .tickSize(0);

    group
      .select('.x-axis')
      .style('transform', `translateY(${+height + 5}px)`)
      .style('color', 'white')
      .call(xAxis)
      .call(g => g.select(".domain").remove())
      .selectAll('.x-axis .tick text')
      .call(function(t){                
        t.each(function(d){ // for each one
          var self = d3.select(this);
          var s = self.text().split(' - ');  // get the text and split it
          self.text(''); // clear it out
          self.append("tspan") // insert two tspans
            .attr("x", 0)
            .attr("dy","1em")
            .text(s[0].slice(0, 7));
          self.append("tspan")
            .attr("x", 0)
            .attr("dy","1em")
            .text(s[1]);
        })});

    group
      .selectAll('.bar')
      .data(data)
      .join('rect')
      .attr('class', 'bar')
      .style('transform', 'scale(1, -1)')
      .attr('x', d => xScale.bandwidth() / 2 - 3 + xScale(d.text))
      // .attr('x', d => xScale(d.text))
      .attr('y', -height)
      .attr('width', 6)
      // .attr('width', xScale.bandwidth())
      .attr('height', d => yScale(Math.abs(d.amount)))
      .attr('fill', d => d.amount < 0 ? colors[0] : colors[1]);


    //#region 
    // const groupWithData = group
    //   .selectAll('.rect')
    //   .data(data);
    // console.log(groupWithData)
    // groupWithData
    //   .exit()
    //   .remove();

    // const groupWithUpdate = groupWithData
    //   .enter()
    //   .append('g')
    //   .attr('class', 'rect')

    // const rect = groupWithUpdate
    //   .append('rect')
    //   .merge(groupWithData.select('rect'));

    // rect
    //   .attr('width', 6)
    //   .attr('height', d => yScale(Math.abs(d.amount)))
    //   .attr('fill', d => d.amount < 0 ? colors[0] : colors[1])
    //   .attr('x', d => xScale.bandwidth() / 2 - 3 + xScale(d.text))
    //   .attr('y', d => height - yScale(Math.abs(d.amount)));
    //#endregion

  // eslint-disable-next-line
  }, [originalData])

  return ( 
    <svg 
      width={width} 
      height={height} 
      ref={ref}
      style={{ margin: 'auto', overflow: 'visible' }}
    >
      <g className='x-axis' />
    </svg>
  );
}

export default BarChart;
