import React from 'react';
import Total from './common/Total';
import PieChart from './common/PieChart';
import { numberEuro } from '../utils/format';

const ReportOverview = ({ selected, timeFilters, value, amounts }) => {

  const income = amounts
    .filter(item => item > 0)
    .reduce((acc, item) => (acc += item), 0)
    .toFixed(2);

  const expense = (amounts
    .filter(item => item < 0)
    .reduce((acc, item) => (acc += item), 0) * -1)
    .toFixed(2);

  const noData = income === '0.00' & expense === '0.00';
  const ratio = income !== '0.00' ? (expense / income * 100).toFixed(0) : 100;
  const data = [ratio, 100 - ratio];

  const width = 120, height = 120;
  const outerRadius = width / 2;
  const innerRadius = outerRadius - 6;

  const balance = { 
    position: 'absolute', 
    top: '50%', left: '50%', 
    transform: 'translate(-50%, -50%)', 
    textAlign: 'center',
    width: '100%',
  }

  const incomeExpense = { 
    display: 'flex', 
    height: '100%', 
    margin: 'auto',
    textAlign: 'center',
    justifyContent: 'space-around',
  }

  const textBlock = {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    height: '100%',
    textAlign: 'right',
    minWidth: '150px',
  }

  const chartBlock = {
    // position: 'relative',
    display: 'flex',
    width: width,
    justifyContent: 'center',
  }

  return (
    <div className='plus-bg' style={{ height: '40vh', position: 'relative' }}>
      {selected === 'all' ? (
        <div style={balance}>
          <Total text={`${timeFilters[value]}ly balance`} amounts={amounts} />
        </div>
      ) : (
        <div className='container' style={incomeExpense}>
          <div style={chartBlock}>
            {!noData 
              ? <p className='sub-amount' style={{ position: 'absolute', alignSelf: 'center', margin: 0 }}>{ratio}%</p> 
              : null
            }
            {!noData 
              ? <div style={{ height: height, alignSelf: 'center' }}>
                  <PieChart
                      data={data}
                      width={width}
                      height={height}
                      outerRadius={outerRadius}
                      innerRadius={innerRadius}
                      style={{ alignSelf: 'center' }}
                    /> 
                </div>
              : <p style={{ color: 'white', alignSelf: 'center' }}>
                  no transaction
                </p>
            }
          </div>

          <div style={textBlock}>
            <div>
              <p className='sub-title' style={{ margin: 0 }}>Income</p>
              <p className='sub-amount'>+{numberEuro(income)}</p>
            </div>
            <div style={{ marginTop: '15px' }}>
              <p className='sub-title' style={{ margin: 0 }}>Expense</p>
              <p className='sub-amount'>-{numberEuro(expense)}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ReportOverview;
