import React, { Fragment, useState, useEffect, useContext } from 'react';
import moment from 'moment';
import { GlobalContext } from '../context/GlobalState';
import Transaction from './Transaction';
import { Tabs, Tab, CircularProgress } from '@material-ui/core';

const Statistics = () => {
  const { loading, transactions, getTransactions } = useContext(GlobalContext);
  const [value, setValue] = useState(0);
  const timeFilters = ['week', 'month', 'year'];
  const incomeLists = [];
  const expenseLists = [];
  const today = moment();

  const sortDate = (type, time) => {
    return (transactions
      .filter(transaction => moment(transaction.date).isSame(today, time))
      .filter(transaction => {
        if (type === 'expense') return transaction.amount < 0;
        if (type === 'income') return transaction.amount > 0;
        if (type === 'all') return transaction.amount;
        return transaction.amount;
      })
    );
  }

  const sumAmount = (data, filter, order) => {
    return Object.values(data
      .reduce((result, { date, amount }) => {
        const format = date => moment(date).format(filter);
        const index = date => moment(date).format(order);

        !result[format(date)] 
          ? (result[format(date)] = { 
              index: +index(date), 
              text: format(date), 
              amount 
            }) 
          : (result[format(date)].amount += amount)

        return result;
    }, {})
  )}
  // const sortedDate = sortDate('income', 'year')
  // console.log(sumAmount(sortedDate, 'MMMM', 'MM'))

  if (value === 0) {
    incomeLists.push(
      sumAmount(sortDate('income', 'week'), 'dddd', 'e')
    );
    expenseLists.push(
      sumAmount(sortDate('expense', 'week'), 'dddd', 'e')
    );
  } else if (value === 1) {
    incomeLists.push(
      sumAmount(sortDate('income', 'month'), 'w', 'w')
    );
    expenseLists.push(
      sumAmount(sortDate('expense', 'month'), 'w', 'w')
    );
  } else if (value === 2) {
    incomeLists.push(
      sumAmount(sortDate('income', 'year'), 'MMMM', 'MM')
    );
    expenseLists.push(
      sumAmount(sortDate('expense', 'year'), 'MMMM', 'MM')
    );
  };

  // const income = (time, filter) => {
  //   const temp = {};
  //   transactions
  //     .filter(transaction => transaction.amount > 0)
  //     .filter(transaction => moment(transaction.date).isSame(today, time))
  //     .map(transaction => {
  //       const date = moment(transaction.date).format(filter);
  //       return temp.hasOwnProperty(date) 
  //         ? temp[date] += transaction.amount
  //         : temp[date] = transaction.amount;
  //     })
  //   Object.entries(temp).forEach(([key, value]) => {
  //     incomeLists.push({ 'text': key, 'amount': value });
  //   });
  // }

  // const expense = (time, filter) => {
  //   const temp = {};
  //   transactions
  //     .filter(transaction => transaction.amount < 0)
  //     .filter(transaction => moment(transaction.date).isSame(today, time))
  //     .map(transaction => {
  //       const date = moment(transaction.date).format(filter);
  //       return temp.hasOwnProperty(date) 
  //         ? temp[date] += transaction.amount
  //         : temp[date] = transaction.amount;
  //     })
  //   Object.entries(temp).forEach(([key, value]) => {
  //     expenseLists.push({ 'text': key, 'amount': value });
  //   });
  // }

  // if (value === 0) {
  //   expense('week', 'e'); income('week', 'e');
  // } else if (value === 1) {
  //   expense('month', 'w'); income('month', 'w');
  // } else if (value === 2) {
  //   expense('year', 'MM'); income('year', 'MM');
  // };

  useEffect(() => {
    getTransactions();
    // eslint-disable-next-line
  }, []);

  return ( 
    <Fragment>
      <div className='plus-bg time-bar'>
        <Tabs value={value} variant="fullWidth" aria-label="time selectors">
          {timeFilters.map((timeFilter, index) => 
            <Tab 
              key={timeFilter} label={timeFilter} 
              onClick={() => setValue(index)} 
              disableFocusRipple disableRipple
            />
          )}
        </Tabs>
      </div>

      <div className='plus-bg box'>
        <div className='container box-incomeExpense'>

        </div>
      </div>

      <div className="container">
        {transactions.length > 0 ? (
          <ul className='list'>
            {incomeLists.concat(expenseLists)
              .sort((a, b) => a.index < b.index ?  -1 : 1)
              .map((list, index) => {
                return <Transaction key={index} transaction={list} />
              })
            }
            {/* {incomeLists.concat(expenseLists)
              .sort((a, b) => a.text < b.text ?  -1 : 1)
              .map((list, index) => {
                if (value === 0) {
                  list.text = moment().day(list.text).format('dddd');
                }
                if (value === 1) {
                  list.text = `${moment().day(0).week(list.text).format('D MMM')} - ${moment().day(6).week(list.text).format('D MMM')}`;
                }
                if (value === 2) {
                  list.text = moment().month(list.text - 1).format('MMMM');
                }

                return <Transaction key={index} transaction={list} />
              })
            } */}

            {/* {expenseLists === 0 && (
              <div className='list-status'>
                No transaction<br/>
                of the {timeFilters[value]}
              </div>
            )} */}
          </ul>
        ) : (
          <div className='list-status'>
            {loading 
              ? <CircularProgress color='primary'/>
              : 'No transaction'}
          </div>
        )}
      </div>
    </Fragment>
  );
}

export default Statistics;
