import React, { useEffect, useRef } from 'react';
import { select, arc, pie, interpolate } from 'd3';

const PieChart = ({ data, width, height, outerRadius, innerRadius }) => {
  const ref = useRef(null);
  const colorList = ['rgba(255, 255, 255, 1)', 'rgba(255, 255, 255, 0.3)'];

  useEffect(() => {
    const svg = select(ref.current);
    const createArc = arc().outerRadius(outerRadius).innerRadius(innerRadius);
    const createPie = pie()
      .value((d) => d)
      .sort(null);
    const instructions = createPie(data);

    // const arcTween = (d, i) => {
    //   const interpolator = d3.interpolate(prevData[i], d);
    //   return (t) => createArc(interpolator(t));
    // };

    // .attrTween("d", function(nextInstruction, index) {
    //   const initialInstruction = pieGenerator([0, 1])[index];
    //   const interpolator = interpolate(
    //     this.lastInstruction || initialInstruction,
    //     nextInstruction
    //   );
    //   this.lastInstruction = interpolator(1);
    //   return function(t) {
    //     return arcGenerator(interpolator(t));
    //   };
    // });

    function arcTween(next, index) {
      const initial = createPie([0, 1])[index];
      const interpolator = interpolate(this.last || initial, next);
      this.last = interpolator(1);
      return (t) => createArc(interpolator(t));
    }

    svg
      .selectAll('.slice')
      .data(instructions)
      .join('path')
      .attr('class', 'slice')
      .attr('fill', (d, i) => colorList[i])
      .transition()
      .duration(1000)
      .attrTween('d', arcTween);

    // eslint-disable-next-line
  }, [data]);

  return (
    <svg width={width} height={height}>
      <g ref={ref} transform={`translate(${outerRadius} ${outerRadius})`} />
    </svg>
  );
};

export default PieChart;
