import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const PieChart = ({ data, width, height, outerRadius, innerRadius }) => {
  const ref = useRef(null);

  const createPie = d3
    .pie()
    .value((d) => d)
    .sort(null);

  const createArc = d3.arc().outerRadius(outerRadius).innerRadius(innerRadius);

  const colors = ['rgba(255, 255, 255, 1)', 'rgba(255, 255, 255, 0.3)'];

  useEffect(() => {
    const svg = d3.select(ref.current);
    svg.selectAll('.slice').data(data).join('path').attr('class', 'slice');

    // eslint-disable-next-line
  }, [data]);

  return (
    <svg width={width} height={height}>
      <g ref={ref} transform={`translate(${outerRadius} ${outerRadius})`} />
    </svg>
  );
};

export default PieChart;
