import moment from 'moment';

export function checkWeekRange(d) {
  const sunday = moment().startOf('week');
  const saturday = moment().endOf('week');
  const today = moment();
  const range = moment().range(sunday, saturday);

  if (today.within(range)) return true;
  return false;
}
