import React from 'react';
import Typography from '@material-ui/core/Typography';
import ArrowDropDownSharpIcon from '@material-ui/icons/ArrowDropDownSharp';
import ArrowDropUpSharpIcon from '@material-ui/icons/ArrowDropUpSharp';

const Filter = ({
  value,
  sortLatest,
  sortDsc,
  handleSortDate,
  handleSortAmount,
  text,
}) => {

  const textItem = {
    display: 'flex',
    flexDirection: 'row',
    textTransform: 'uppercase',
  };

  const sortItem = {
    display: 'flex',
    flexDirection: 'row',
    textTransform: 'uppercase',
    cursor: 'pointer',
  };

  const alignItem = {
    alignSelf: 'center',
  };

  return (
    <div className='input-amount plus' style={{ margin: '10px 0' }}>
      {value === 0 ? (
        <div style={textItem}>
          <Typography variant='body2'>{text}</Typography>
        </div>
      ) : (
        <div style={sortItem} onClick={handleSortDate}>
          <Typography variant='body2' style={alignItem}>
            sort by Date
          </Typography>
          {sortLatest ? <ArrowDropDownSharpIcon /> : <ArrowDropUpSharpIcon />}
        </div>
      )}
      <div style={sortItem} onClick={handleSortAmount}>
        <Typography variant='body2' style={alignItem}>
          Amount
        </Typography>
        {sortDsc ? <ArrowDropDownSharpIcon /> : <ArrowDropUpSharpIcon />}
      </div>
    </div>
  );
};

export default Filter;
