import React, { Fragment, useState, useEffect, useContext } from 'react';
import moment from 'moment';
import { GlobalContext } from '../context/GlobalState';
import { checkWeek, checkMonth, checkYear } from '../utils/calculation';
import Transaction from './Transaction';
import { Tabs, Tab, CircularProgress } from '@material-ui/core';

const Statistics = () => {
  const { loading, transactions, getTransactions } = useContext(GlobalContext);
  const [value, setValue] = useState(0);
  // const [selected, setSelected] = useState('all');
  // const [sortLatest, setSortLatest] = useState(true);
  // const [sortDsc, setSortDsc] = useState(true);

  const timeFilters = ['week', 'month', 'year'];
  // const filterLists = [
  //   { value: 0, time: 'week', filter: 'dddd'},
  //   { value: 1, time: 'month', filter: 'ww'},
  //   { value: 2, time: 'year', filter: 'MMMM'},
  // ];

  const today = moment();
  const incomeLists = [];
  const expenseLists = [];

  const income = (time, filter) => {
    const temp = {};

    transactions
      .filter(transaction => transaction.amount > 0)
      .filter(transaction => moment(transaction.date).isSame(today, time))
      .map(transaction => {
        const date = moment(transaction.date).format(filter);
        if (transaction.amount > 0) {
          temp.hasOwnProperty(date) 
            ? temp[date] += transaction.amount
            : temp[date] = transaction.amount;
        }
      })

    Object.entries(temp).forEach(([key, value], index) => {
      incomeLists.push({ 
        '_id': index, 
        'text': `${key}`, 
        'amount': `${value}` 
      });
    });
  }

  const expense = (time, filter) => {
    const temp = {};

    transactions
      .filter(transaction => transaction.amount < 0)
      .filter(transaction => moment(transaction.date).isSame(today, time))
      .map(transaction => {
        const date = moment(transaction.date).format(filter);
        if (temp.hasOwnProperty(date)) {
          return temp[date] += transaction.amount;
        } else {
          return temp[date] = transaction.amount;
        }
      })

    Object.entries(temp).forEach(([key, value], index) => {
      expenseLists.push({ 
        '_id': index, 
        'text': `${key}`, 
        'amount': `${value}` 
      });
    });
  }

  if (value === 0) expense('week', 'dddd'); income('week', 'dddd');
  if (value === 1) expense('month', 'ww'); income('month', 'ww');
  if (value === 2) expense('year', 'MMMM'); income('year', 'MMMM');

  useEffect(() => {
    getTransactions();
    // eslint-disable-next-line
  }, []);

  return ( 
    <Fragment>
      <div className='plus-bg time-bar'>
        <Tabs value={value} variant="fullWidth" aria-label="time selectors">
          {timeFilters.map((timeFilter, index) => 
            <Tab 
              key={timeFilter} label={timeFilter} 
              onClick={() => setValue(index)} 
              disableFocusRipple disableRipple
            />
          )}
        </Tabs>
      </div>

      <div className='plus-bg box'>
        <div className='container box-incomeExpense'>

        </div>
      </div>

      <div className="container">
        {transactions.length > 0 ? (
          <ul className='list'>

            {incomeLists.map(list => 
              <Transaction key={list.text + list._id} transaction={list} />
            )}

            {expenseLists.map(list => 
              <Transaction key={list.text + list._id} transaction={list} />
            )}

            {/* {expenseLists === 0 && (
              <div className='list-status'>
                No transaction<br/>
                of the {timeFilters[value]}
              </div>
            )} */}
          </ul>
        ) : (
          <div className='list-status'>
            {loading 
              ? <CircularProgress color='primary'/>
              : 'No transaction'}
          </div>
        )}
      </div>
    </Fragment>
  );
}

export default Statistics;
